mapper.update:
UPDATE QNA


		BOARDNAME = #{boardName},

	BOARDCONTENTS = #{boardContents}

WHERE BOARDNUM = #{boardNum}

mapper.select:
SELECT * FROM
(SELECT ROWNUM R, Q.* FROM

(SELECT * FROM QNA



ORDER BY BOARDREF DESC, BOARDSTEP ASC)Q

)RQ
WHERE R BETWEEN #{startRow}
and #{lastRow}

mapper.update:
UPDATE QNA SET FLAG
=#{flag}
WHERE BOARDNUM = #{boardNum}
